mapper.select:
select user_id, user_name, real_name, user_sex, user_email, user_mobile, work_area, work_type, work_unit, work_tel, id_card, application_area, address, fax, country, province, city, status, audit_status, audit_fail_reason, SOURCE, founder, role_id, to_char(create_time, 'yyyy/MM/dd HH:mm:ss') create_time from ddss_sys_user
AND user_name like '%${userName}%'
AND real_name=#{realName}
AND user_email like '%${userEmail}%'
AND work_unit like '%${workUnit}%'
AND role_id = #{roleId}
AND audit_status = #{auditStatus}
AND status != 2
ORDER BY user_id ASC
ORDER BY user_id DESC
ORDER BY user_id DESC
limit #{pageSize} offset #{startNum}

  	 select 
    count(1)
    from ddss_sys_user
    
    		
